INSERT INTO `comment` VALUES (6,'',NULL,'2018-03-20 20:52:26',6,'kevin.Z',1),(7,'原来点回车键就会直接上传数据了……<br/>挺方便的嘛，下次写完 twitter 就不用弄鼠标了。',NULL,'2018-03-20 20:53:23',6,'晓翔',1),(8,'需不需要去看心理医生来的？<br/>呃，应该是不用的吧……<br/>这样子活着也挺开心的？哈哈                            ',NULL,'2018-03-21 13:16:43',7,'kevin',1),(9,'无聊的一篇文章，可以删了的',NULL,'2018-06-01 21:07:04',8,'kevin',1),(10,'我去，真的忘记了，我的标题起的是十年之约，写写十年前对自己无比失望的时刻是什么样子的，和写写十年后对自己的期待……天呐！写作真的需要锻炼啊！！！',NULL,'2018-08-08 03:42:10',21,'晓翔',1);
